import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';

const App = () => {
  return (
    <View style={styles.container}>
      <Text style={styles.header}>Aplikacija za lov</Text>
      <View style={styles.menu}>
        <MenuItem icon="account-box-outline" text="EVIDENCIJA LOVACA" />
        <MenuItem icon="calendar" text="RASPORED AKTIVNOSTI I LOVA" />
        <MenuItem icon="clipboard-list-outline" text="EVIDENCIJA PRISUTNOSTI NA LOVNI DAN" />
        <MenuItem icon="paw" text="UNOS DIVLJAČI" />
        <MenuItem icon="target" text="UNOS ODSTRIJELENE DIVLJAČI" />
        <MenuItem icon="numeric-7-circle-outline" text="BODOVI" />
      </View>
    </View>
  );
};

const MenuItem = ({ icon, text }) => {
  return (
    <TouchableOpacity style={styles.menuItem}>
      <Icon name={icon} style={styles.menuIcon} />
      <Text style={styles.menuText}>{text}</Text>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  header: {
    textAlign: 'center',
    margin: 20,
    fontSize: 22,
    fontWeight: 'bold',
  },
  menu: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    alignItems: 'flex-start',
  },
  menuItem: {
    width: '50%', // each menu item takes up half of the container width
    padding: 10,
    justifyContent: 'center',
    alignItems: 'center',
  },
  menuIcon: {
    fontSize: 40,
    marginBottom: 8,
  },
  menuText: {
    textAlign: 'center',
  },
});

export default App;
