import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";

function DataEntryForm() {
    const [zivotinje, setZivotinje] = useState([]);
    const [sifraZivotinje, setSifraZivotinje] = useState('');
    const [vrijemeOdstrela, setVrijemeOdstrela] = useState(new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }));
    const [datumOdstrela, setDatumOdstrela] = useState(new Date().toISOString().split('T')[0]);
    const [lokacijaOdstrela, setLokacijaOdstrela] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://localhost:3000/popis-zivotinja')
            .then(response => setZivotinje(response.data))
            .catch(error => console.error('Error fetching animals:', error));
    }, []);

    const handleSubmit = (event) => {
        event.preventDefault();
        const token = localStorage.getItem('token');

        const config = {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        };

        const data = {
            sifra_zivotinje: sifraZivotinje,
            vrijeme_odstrela: vrijemeOdstrela,
            datum_odstrela: datumOdstrela,
            lokacija_odstrela: lokacijaOdstrela
        };

        axios.post('http://localhost:3000/unos-ostreljene-zivotinje', data, config)
            .then(response => {
                alert('Odstreljena životinja je dodana.');
                navigate("/popis-ostreljenih");
            })
            .catch(error => {
                console.error('There was an error!', error);
                setMessage(`Error: ${error.message}`);
            });
    };

    return (
        <div style={{ padding: '20px', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#eee' }}>
            <h1>Unos Odstreljene Životinje</h1>
            <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', margin: '50px', padding: '20px', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0,0,0,0.1)', backgroundColor: '#f7f7f7' }}>
                <div>
                    <label>Vrsta životinje:</label>
                    <select onChange={(e) => setSifraZivotinje(e.target.value)} required>
                        {zivotinje.map(zivotinja => (
                            <option key={zivotinja.sifra_zivotinje} value={zivotinja.sifra_zivotinje}>
                                {zivotinja.vrsta_zivotinje}
                            </option>
                        ))}
                    </select>
                </div>
                <div>
                    <label>Vrijeme odstrela:</label>
                    <input type="time" value={vrijemeOdstrela} onChange={(e) => setVrijemeOdstrela(e.target.value)} required />
                </div>
                <div>
                    <label>Datum odstrela:</label>
                    <input type="date" value={datumOdstrela} onChange={(e) => setDatumOdstrela(e.target.value)} required />
                </div>
                <div>
                    <label>Lokacija odstrela:</label>
                    <input type="text" value={lokacijaOdstrela} onChange={(e) => setLokacijaOdstrela(e.target.value)} required />
                </div>
                <button type="submit">Dodaj Odstrel</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}

export default DataEntryForm;
