import React, { useEffect, useState } from 'react';
import axios from 'axios';

function PopisLovaca() {
    const [lovci, setLovci] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3000/popis-lovaca')
            .then(response => {
                if (response.data.error) {
                    console.error('Failed to fetch hunters:', response.data.message);
                    alert('Error: ' + response.data.message);
                } else {
                    setLovci(response.data.data);
                }
            })
            .catch(error => {
                console.error('There was an error!', error);
            });
    }, []);

    const handleDelete = (broj_lovacke_iskaznice) => {
        const isConfirmed = window.confirm("Želite li zaista obrisati lovca?");
        if (isConfirmed) {
            axios
                .delete(`http://localhost:3000/lovac-brisi/${broj_lovacke_iskaznice}`)
                .then(() => {
                    setLovci(prevLovci => prevLovci.filter(lovac => lovac.broj_lovacke_iskaznice !== broj_lovacke_iskaznice));
                    console.log("Deleted successfully");
                })
                .catch((err) => {
                    console.error('Error deleting hunter:', err);
                    alert('Error deleting hunter');
                });
        }
    };

    return (
        <div>
            <h1>Popis Lovaca</h1>
            <table>
                <thead>
                    <tr>
                        <th>Broj iskaznice</th>
                        <th>Ime</th>
                        <th>Prezime</th>
                        <th>Adresa</th>
                        <th>Datum rođenja</th>
                        <th>Kontakt</th>
                        <th>Korisničko ime</th>
                        <th>Uloga</th>
                        <th>Akcije</th>
                    </tr>
                </thead>
                <tbody>
                    {lovci.map((lovac) => (
                        <tr key={lovac.broj_lovacke_iskaznice}>
                            <td>{lovac.broj_lovacke_iskaznice}</td>
                            <td>{lovac.ime}</td>
                            <td>{lovac.prezime}</td>
                            <td>{lovac.adresa}</td>
                            <td>{lovac.datum_rodjenja}</td>
                            <td>{lovac.kontakt}</td>
                            <td>{lovac.korisnicko_ime}</td>
                            <td>{lovac.uloga}</td>
                            <td><button onClick={() => handleDelete(lovac.broj_lovacke_iskaznice)}>Delete</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default PopisLovaca;
