import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";

function DataEntryForm() {
    const [animalTypes, setAnimalTypes] = useState([]);
    const [selectedAnimalType, setSelectedAnimalType] = useState('');
    const [newAnimalType, setNewAnimalType] = useState('');
    const [newAnimalDescription, setNewAnimalDescription] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch animal types when the component mounts
        axios.get('http://localhost:3000/vrste-zivotinja')
            .then(response => {
                setAnimalTypes(response.data.data);
            })
            .catch(error => {
                console.error('Error fetching animal types!', error);
            });
    }, []);

    const handleSubmit = (event) => {
        event.preventDefault();
        const animalData = {
            vrsta_zivotinje: newAnimalType || selectedAnimalType,
            opis_zivotinje: newAnimalDescription
        };

        // Retrieve the token from local storage
        const token = localStorage.getItem('token');

        // Set up the configuration for the axios request including the Authorization header
        const config = {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        };

        axios.post('http://localhost:3000/unos-zivotinje', animalData, config)
            .then(response => {
                alert('Životinja je dodana.');
                navigate("/popis-divljaci");
            })
            .catch(error => {
                console.error('There was an error!', error);
                setMessage(`Error: ${error.message}`);
            });
    };

    // Styles
    const formStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        margin: '50px',
        padding: '20px',
        borderRadius: '8px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
        backgroundColor: '#f7f7f7'
    };

    const inputStyle = {
        margin: '10px 0',
        padding: '10px',
        width: '300px',
        borderRadius: '5px',
        border: '1px solid #ccc'
    };

    const buttonStyle = {
        padding: '10px 20px',
        fontSize: '16px',
        color: 'white',
        backgroundColor: '#007BFF',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer'
    };

    const labelStyle = {
        margin: '10px 0',
        fontWeight: 'bold'
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#eee' }}>
            <form onSubmit={handleSubmit} style={formStyle}>
                <h1 style={{ color: '#333' }}>Unos životinja</h1>
                <div>
                    <label style={labelStyle}>Odaberi postojeću vrstu životinje:</label>
                    <select
                        value={selectedAnimalType}
                        onChange={(e) => { setSelectedAnimalType(e.target.value); setNewAnimalType(''); }}
                        style={inputStyle}
                    >
                        <option value="">Odaberi vrstu</option>
                        {animalTypes.map((animal) => (
                            <option key={animal.sifra_zivotinje} value={animal.vrsta_zivotinje}>{animal.vrsta_zivotinje}</option>
                        ))}
                    </select>
                </div>
                <div>
                    <label style={labelStyle}>Ili unesi novu vrstu životinje:</label>
                    <input
                        type="text"
                        value={newAnimalType}
                        onChange={(e) => { setNewAnimalType(e.target.value); setSelectedAnimalType(''); }}
                        style={inputStyle}
                    />
                </div>
                <div>
                    <label style={labelStyle}>Opis životinje:</label>
                    <input
                        type="text"
                        value={newAnimalDescription}
                        onChange={(e) => setNewAnimalDescription(e.target.value)}
                        required={newAnimalType}
                        style={inputStyle}
                    />
                </div>
                <button type="submit" style={buttonStyle}>Unesi</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}

export default DataEntryForm;
