import React, { useState } from 'react';
import { View, TextInput, Button, StyleSheet } from 'react-native';

const App = () => {
  const [vrsta, setVrsta] = useState('');
  const [opis, setOpis] = useState('');

  const handleSubmit = async () => {
    const response = await fetch('http://<YOUR_SERVER_IP>:3000/addAnimal', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        vrsta_zivotinje: vrsta,
        opis_zivotinje: opis
      })
    });
    const responseData = await response.text();
    alert(responseData);
  };

  return (
    <View style={styles.container}>
      <TextInput
        placeholder="Vrsta životinje"
        style={styles.input}
        value={vrsta}
        onChangeText={setVrsta}
      />
      <TextInput
        placeholder="Opis životinje"
        style={styles.input}
        value={opis}
        onChangeText={setOpis}
      />
      <Button title="Spremi" onPress={handleSubmit} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    padding: 20
  },
  input: {
    height: 40,
    marginBottom: 12,
    borderWidth: 1,
    padding: 10,
  }
});

export default App;
