import React, { useState } from 'react';
import { QRCode } from 'qrcode.react';

function QRCodeDisplay({ token }) {
    const [huntersLicenseNumber, setHuntersLicenseNumber] = useState('');

    // Dekodiranje JWT tokena da dobijemo broj lovačke iskaznice
    // Ovaj korak pretpostavlja da imate funkciju za dekodiranje JWT tokena na klijentu
    // Alternativno, server bi mogao poslati broj iskaznice direktno umjesto cijelog tokena
    React.useEffect(() => {
        const decodeToken = async () => {
            const decoded = jwt_decode(token); // Pretpostavljamo da imate jwt_decode funkciju
            setHuntersLicenseNumber(decoded.id);
        };
        decodeToken();
    }, [token]);

    return (
        <div>
            <h1>Your QR Code</h1>
            {huntersLicenseNumber ? (
                <QRCode value={huntersLicenseNumber} size={256} />
            ) : (
                <p>Loading QR Code...</p>
            )}
        </div>
    );
}

export default QRCodeDisplay;
