import React, { useState, useEffect } from 'react';
import { QRCodeSVG } from 'qrcode.react';
import jwt_decode from 'jwt-decode';  // Import the jwt-decode library

function QRCodeDisplay({ token }) {
    const [huntersLicenseNumber, setHuntersLicenseNumber] = useState('');

    // Decode JWT token to get the hunter's license number
    useEffect(() => {
        const decodeToken = () => {
            const decoded = jwt_decode(token);
            setHuntersLicenseNumber(decoded.id);
        };
        decodeToken();
    }, [token]);

    return (
        <div>
            <h1>Your QR Code</h1>
            {huntersLicenseNumber ? (
                <QRCodeSVG value={huntersLicenseNumber} size={256} />
            ) : (
                <p>Loading QR Code...</p>
            )}
        </div>
    );
}

export default QRCodeDisplay;
