// PopisDivljaci.js
import React, { useState } from 'react';
import axios from 'axios';

function PopisDivljaci() {
    const [vrsta, setVrsta] = useState('');
    const [opis, setOpis] = useState('');

    const handleVrstaChange = (e) => {
        setVrsta(e.target.value);
    };

    const handleOpisChange = (e) => {
        setOpis(e.target.value);
    };

    const handleSubmit = () => {
        axios.post('http://193.198.97.21:5000/dodajZivotinju', {
            vrsta,
            opis
        })
            .then(response => {
                console.log('Životinja dodana', response.data);
                alert('Životinja je uspješno dodana!');
            })
            .catch(error => {
                console.error('Došlo je do greške pri dodavanju životinje', error);
                alert('Greška pri dodavanju životinje!');
            });
    };

    const handleReset = () => {
        setVrsta('');
        setOpis('');
    };

    return (
        <div>
            <h1>Unos Divljih Životinja</h1>
            <label>
                Vrsta:
                <input type="text" value={vrsta} onChange={handleVrstaChange} />
            </label>
            <label>
                Opis:
                <input type="text" value={opis} onChange={handleOpisChange} />
            </label>
            <button onClick={handleSubmit}>Dodaj Životinju</button>
            <button onClick={handleReset}>Resetiraj</button>
        </div>
    );
}

export default PopisDivljaci;
