import React, { useState, useEffect } from 'react';
import QRCode from 'qrcode.react';
import { decode as jwtDecode } from 'jwt-decode'; // Corrected import

function QRCodeDisplay({ token }) {
    const [brojLovackeIskaznice, setBrojLovackeIskaznice] = useState('');

    useEffect(() => {
        if (token) {
            const decoded = jwtDecode(token);
            setBrojLovackeIskaznice(decoded.id); // Assuming 'id' contains broj_lovacke_iskaznice
        }
    }, [token]);

    return (
        <div>
            {brojLovackeIskaznice ? (
                <QRCode value={brojLovackeIskaznice.toString()} size={256} level={"H"} includeMargin={true} />
            ) : (
                <p>No data available</p>
            )}
        </div>
    );
}

export default QRCodeDisplay;
