import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import CollapsableNavbar from "../components/CollapsableNavbar"; // Assuming you have a similar component

function PopisLovaca() {
    const [lovci, setLovci] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        const config = {
            headers: { 'Authorization': `Bearer ${token}` }
        };

        axios.get('http://localhost:3000/popis-lovaca', config)
            .then(response => {
                if (response.data.error) {
                    console.error('Failed to fetch hunters:', response.data.message);
                    alert('Error: ' + response.data.message);
                } else {
                    setLovci(response.data.data);
                }
            })
            .catch(error => {
                console.error('There was an error!', error);
            });
    }, []);

    return (
        <div>
            <CollapsableNavbar />
            <h1 style={{ textAlign: 'center' }}>Popis Lovaca</h1>
            <table className="table table-striped table-hover">
                <thead>
                    <tr>
                        <th>Ime</th>
                        <th>Prezime</th>
                        <th>Adresa</th>
                        <th>Datum rođenja</th>
                        <th>Kontakt</th>
                        <th>Korisničko ime</th>
                        <th>Uloga</th>
                        <th>Briši</th>
                    </tr>
                </thead>
                <tbody>
                    {lovci.map((lovac, index) => (
                        <tr key={index}>
                            <td>{lovac.ime}</td>
                            <td>{lovac.prezime}</td>
                            <td>{lovac.adresa}</td>
                            <td>{lovac.datum_rodjenja}</td>
                            <td>{lovac.kontakt}</td>
                            <td>{lovac.korisnicko_ime}</td>
                            <td>{lovac.uloga}</td>
                            <td>
                                <button
                                    className="btn btn-danger"
                                    onClick={() => handleDelete(lovac.broj_lovacke_iskaznice)}
                                >
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default PopisLovaca;
