import React, { useState } from 'react';
import { View, Button, StyleSheet } from 'react-native';
import { RNCamera } from 'react-native-camera';
import { check, PERMISSIONS, request, RESULTS } from 'react-native-permissions';

const QRCodeScanner = () => {
    const [cameraActive, setCameraActive] = useState(false);

    const handlePress = async () => {
        const cameraPermission = await check(PERMISSIONS.IOS.CAMERA);

        if (cameraPermission === RESULTS.DENIED) {
            const newPermission = await request(PERMISSIONS.IOS.CAMERA);
            if (newPermission === RESULTS.GRANTED) {
                setCameraActive(true);
            }
        } else if (cameraPermission === RESULTS.GRANTED) {
            setCameraActive(true);
        } else {
            // Handle other cases if necessary
            alert('Camera access is denied');
        }
    };

    const handleQRCodeRead = e => {
        alert(`QR Code Detected: ${e.data}`);
        setCameraActive(false); // Close camera after scanning
    };

    return (
        <View style={styles.container}>
            <Button title="Scan QR Code" onPress={handlePress} />
            {cameraActive && (
                <RNCamera
                    style={styles.preview}
                    onBarCodeRead={handleQRCodeRead}
                    type={RNCamera.Constants.Type.back}
                    captureAudio={false}
                />
            )}
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: 'column',
        justifyContent: 'center',
    },
    preview: {
        flex: 1,
        justifyContent: 'flex-end',
        alignItems: 'center'
    }
});

export default QRCodeScanner;
