import React, { useEffect, useState } from 'react';
import QRCode from 'react-qr-code';
import jwt_decode from 'jwt-decode';

function App() {
    const [lovackaIskaznica, setLovackaIskaznica] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token');  // Dohvatite token iz localStorage
        if (token) {
            const decoded = jwt_decode(token);  // Dekodirajte token
            setLovackaIskaznica(decoded.id);  // Podesite broj lovačke iskaznice
        }
    }, []);

    return (
        <div className="App">
            <h1>QR Code Example</h1>
            <QRCode value={lovackaIskaznica || 'No ID'} size={256} />
        </div>
    );
}

export default App;
