import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

function PopisLovaca() {
    const [lovci, setLovci] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate("/prijava");
            return;
        }

        axios.get('http://localhost:3000/verify-token?roles=admin', {
            headers: { 'Authorization': `Bearer ${token}` }
        }).then(response => {
            if (response.status === 200) {
                axios.get('http://localhost:3000/popis-lovaca', {
                    headers: { 'Authorization': `Bearer ${token}` }
                }).then(response => {
                    setLovci(response.data.data);
                }).catch(error => {
                    console.error('Failed to fetch hunters:', error);
                });
            } else {
                navigate("/forbidden");
            }
        }).catch(() => {
            navigate("/prijava");
        });
    }, [navigate]);

    const handleDelete = (broj_lovacke_iskaznice) => {
        const token = localStorage.getItem('token');
        const confirmDelete = window.confirm("Želite li zaista obrisati lovca?");
        if (!confirmDelete) return;

        axios.delete(`http://localhost:3000/lovac/${broj_lovacke_iskaznice}`, {
            headers: { 'Authorization': `Bearer ${token}` }
        }).then(() => {
            setLovci(prevLovci => prevLovci.filter(lovac => lovac.broj_lovacke_iskaznice !== broj_lovacke_iskaznice));
            console.log("Deleted successfully");
        }).catch(error => {
            alert("Došlo je do pogreške prilikom brisanja!");
            console.error('Error deleting hunter:', error);
        });
    };

    return (
        <div className="container-fluid">
            <h1 style={{ textAlign: 'center' }}>Popis Lovaca</h1>
            <Link to="/unos-lovca" className="btn btn-sm btn-primary mb-3">
                Unesi lovca
            </Link>
            <div className="table-responsive">
                <table className="table table-striped table-hover">
                    <thead>
                        <tr>
                            <th>Ime</th>
                            <th>Prezime</th>
                            <th>Adresa</th>
                            <th>Datum rođenja</th>
                            <th>Kontakt</th>
                            <th>Korisničko ime</th>
                            <th>Uloga</th>
                            <th>Akcije</th>
                        </tr>
                    </thead>
                    <tbody>
                        {lovci.map((lovac) => (
                            <tr key={lovac.broj_lovacke_iskaznice}>
                                <td>{lovac.ime}</td>
                                <td>{lovac.prezime}</td>
                                <td>{lovac.adresa}</td>
                                <td>{lovac.datum_rodjenja}</td>
                                <td>{lovac.kontakt}</td>
                                <td>{lovac.korisnicko_ime}</td>
                                <td>{lovac.uloga}</td>
                                <td>
                                    <button className="btn btn-sm btn-danger" onClick={() => handleDelete(lovac.broj_lovacke_iskaznice)}>
                                        Izbriši
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default PopisLovaca;
