// unos.js
import React, { useState, useRef } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Webcam from 'react-webcam';
import jsQR from 'jsqr';

function DataEntryForm() {
    const [brojLovackeIskaznice, setBrojLovackeIskaznice] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();
    const webcamRef = useRef(null);

    // Constraints to use the rear-facing camera
    const videoConstraints = {
        facingMode: { exact: "environment" }
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const userData = {
            broj_lovacke_iskaznice: brojLovackeIskaznice
        };

        const token = localStorage.getItem('token');

        const config = {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        };

        axios.post('https://c1ea478869cf.ngrok.app/unos-osobe-u-lov', userData, config)
            .then(response => {
                alert('Osoba je dodana u prisutnost.');
                navigate("/popis-prisutnosti");
            })
            .catch(error => {
                console.error('There was an error!', error);
                setMessage(`Error: ${error.message}`);
            });
    };

    const capture = () => {
        const imageSrc = webcamRef.current.getScreenshot();
        if (imageSrc) {
            const image = new Image();
            image.src = imageSrc;
            image.onload = () => {
                const canvas = document.createElement('canvas');
                const context = canvas.getContext('2d');
                canvas.width = image.width;
                canvas.height = image.height;
                context.drawImage(image, 0, 0, canvas.width, canvas.height);
                const imageData = context.getImageData(0, 0, canvas.width, canvas.height);
                const qrCode = jsQR(imageData.data, imageData.width, imageData.height, {
                    inversionAttempts: 'dontInvert',
                });
                if (qrCode) {
                    setBrojLovackeIskaznice(qrCode.data);
                } else {
                    setMessage('Nema QR koda u vidnom polju');
                }
            };
        }
    };

    const formStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        margin: '50px',
        padding: '20px',
        borderRadius: '8px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
        backgroundColor: '#f7f7f7'
    };

    const inputStyle = {
        margin: '10px 0',
        padding: '10px',
        width: '300px',
        borderRadius: '5px',
        border: '1px solid #ccc'
    };

    const buttonStyle = {
        padding: '10px 20px',
        fontSize: '16px',
        color: 'white',
        backgroundColor: '#007BFF',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer'
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#eee' }}>
            <form onSubmit={handleSubmit} style={formStyle}>
                <h1 style={{ color: '#333' }}>Unos osobe u lov</h1>
                <div>
                    <label style={{ margin: '10px 0', fontWeight: 'bold' }}>Broj lovaƒçke iskaznice:</label>
                    <input
                        type="text"
                        value={brojLovackeIskaznice}
                        onChange={(e) => setBrojLovackeIskaznice(e.target.value)}
                        required
                        style={inputStyle}
                    />
                </div>
                <button type="submit" style={buttonStyle}>Unesi</button>
                <br />
                <button onClick={() => navigate('/popis-prisutnosti')} style={buttonStyle}>
                    Odustani
                </button>
                <br />
                <h2>Skeniraj QR kod</h2>
                <Webcam
                    audio={false}
                    ref={webcamRef}
                    screenshotFormat="image/jpeg"
                    videoConstraints={videoConstraints}
                    style={{ width: '100%' }}
                />
                <button onClick={capture} style={buttonStyle}>Skeniraj QR kod</button>
                <p>{message}</p>
            </form>
        </div>
    );
}

export default DataEntryForm;
