const handleSubmit = async (event) => {
    event.preventDefault(); // Prevents page refresh

    try {
        // Notice we are not specifying full URL here thanks to proxy configuration
        const response = await fetch('http://localhost:3001/zivotinje', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ vrsta, opis }),
        });

        if (response.ok) {
            const result = await response.json();
            console.log('Successfully added animal:', result);
            setVrsta('');
            setOpis('');
            // Here you can add logic to notify the user of success
        } else {
            throw new Error(`Request failed: ${response.status}`);
        }
    } catch (error) {
        console.error('There was an error sending the data:', error);
        // Here you can add logic to display the error to the user
    }
};
