import React, { useState, useEffect } from 'react';
import { QRCodeSVG } from 'qrcode.react';
import { jwtDecode } from 'jwt-decode';

function QRCodeDisplay({ token }) {
    const [huntersLicenseNumber, setHuntersLicenseNumber] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        if (!token) {
            setError('Token not provided.');
            return;
        }

        try {
            const decoded = jwtDecode(token);
            setHuntersLicenseNumber(decoded.id);
        } catch (error) {
            console.error("Error decoding token:", error);
            setError('Failed to decode token.');
        }
    }, [token]);

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h1>Your QR Code</h1>
            {huntersLicenseNumber ? (
                <QRCodeSVG value={huntersLicenseNumber} size={256} />
            ) : (
                <p>Loading QR Code...</p>
            )}
        </div>
    );
}

export default QRCodeDisplay;
