import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function PopisLovaca() {
    const [lovci, setLovci] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        const config = {
            headers: { 'Authorization': `Bearer ${token}` }
        };

        axios.get('http://localhost:3000/popis-lovaca', config)
            .then(response => {
                console.log("Received data:", response.data.data); // Debug: Log fetched data
                if (response.data.error) {
                    console.error('Failed to fetch hunters:', response.data.message);
                    alert('Error: ' + response.data.message);
                } else {
                    setLovci(response.data.data);
                }
            })
            .catch(error => {
                console.error('There was an error!', error);
            });
    }, []);

    const handleDelete = (broj_lovacke_iskaznice) => {
        console.log("Attempting to delete hunter with ID:", broj_lovacke_iskaznice); // Debug: Confirm ID

        if (!broj_lovacke_iskaznice) {
            alert('Broj lovačke iskaznice is undefined!');
            return;
        }

        const token = localStorage.getItem('token');
        const config = {
            headers: { 'Authorization': `Bearer ${token}` }
        };

        axios.delete(`http://localhost:3000/lovac/${broj_lovacke_iskaznice}`, config)
            .then(response => {
                if (response.data.error) {
                    console.error('Error deleting hunter:', response.data.message);
                    alert('Error deleting hunter: ' + response.data.message);
                } else {
                    setLovci(prevLovci => prevLovci.filter(lovac => lovac.broj_lovacke_iskaznice !== broj_lovacke_iskaznice));
                    console.log("Deleted successfully");
                }
            })
            .catch(error => {
                console.error('Error deleting hunter:', error);
                alert('Error deleting hunter');
            });
    };

    return (
        <div style={{ padding: '20px', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#eee' }}>
            <button onClick={() => navigate('/unos-lovca')} style={{ padding: '20px 50px', fontSize: '12px', color: 'white', backgroundColor: '#007BFF', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Unesi lovca</button>
            <h1 style={{ color: '#333', textAlign: 'center' }}>Popis Lovaca</h1>
            <table style={{ width: '100%', borderCollapse: 'collapse', boxShadow: '0 4px 8px rgba(0,0,0,0.1)', backgroundColor: '#f7f7f7' }}>
                <thead>
                    <tr>
                        <th style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'left' }}>Ime</th>
                        ...
                        <th style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'left' }}>Briši</th>
                    </tr>
                </thead>
                <tbody>
                    {lovci.map((lovac) => (
                        <tr key={lovac.broj_lovacke_iskaznice}>
                            ...
                            <td><button onClick={() => handleDelete(lovac.broj_lovacke_iskaznice)} style={{ padding: '20px 50px', fontSize: '12px', color: 'white', backgroundColor: '#FF6347', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Delete</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default PopisLovaca;
