const express = require('express');
const mysql = require('mysql');
const bodyParser = require('body-parser');

const app = express();
app.use(bodyParser.json());

// MySQL konfiguracija
const db = mysql.createConnection({
  host: '193.198.97.21',
  user: 'vdenona',
  password: '11',
  database: 'vdenona'
});

// Povezivanje na bazu
db.connect((err) => {
  if (err) {
    throw err;
  }
  console.log('Connected to database');
});

// API ruta za dodavanje podataka
app.post('/addAnimal', (req, res) => {
  let data = { vrsta_zivotinje: req.body.vrsta_zivotinje, opis_zivotinje: req.body.opis_zivotinje };
  let sql = 'INSERT INTO Zivotinja SET ?';
  db.query(sql, data, (err, results) => {
    if (err) throw err;
    res.send('Animal added successfully');
  });
});

const PORT = 3000;
app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});
