const express = require('express');
const bodyParser = require('body-parser');
const mysql = require('mysql');

const app = express();
const port = 3000;

// Povezivanje s bazom podataka
const pool = mysql.createPool({
  connectionLimit: 10,
  host: '193.198.97.21',
  user: 'vdenona',
  password: '11',
  database: 'vdenona'
});

// Postavke za prikaz stranica
app.set('view engine', 'ejs');
app.use(bodyParser.urlencoded({ extended: true }));

// Ruta za početnu stranicu
app.get('/', (req, res) => {
  res.render('index');
});

// Ruta za obradu forme
app.post('/add', (req, res) => {
  let naziv = req.body.naziv;
  let opis = req.body.opis;
  if (naziv && opis) {
    pool.query('INSERT INTO Zivotinja (naziv_zivotinje, opis_zivotinje) VALUES (?, ?)', [naziv, opis], (error, results, fields) => {
      if (error) throw error;
      res.send('Podaci su uspješno dodani u bazu.');
    });
  } else {
    res.send('Molimo unesite sve podatke.');
  }
});

// Pokretanje servera
app.listen(port, () => {
  console.log(`Server pokrenut na portu ${port}`);
});
