import React, { useState } from 'react';

function UnosPodataka() {
    const [vrsta, setVrsta] = useState('');
    const [opis, setOpis] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('/api/zivotinja', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ vrsta, opis }),
            });
            const data = await response.json();
            console.log('Podaci su poslani:', data);
            alert('Podaci su uspješno spremljeni.');
            setVrsta('');
            setOpis('');
        } catch (error) {
            console.error('Greška pri slanju podataka:', error);
            alert('Došlo je do greške pri slanju podataka.');
        }
    };

    return (
        <div>
            <h1>Unos Podataka o Životinjama</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Vrsta životinje:
                    <input
                        type="text"
                        value={vrsta}
                        onChange={(e) => setVrsta(e.target.value)}
                        placeholder="Unesi vrstu životinje"
                    />
                </label>
                <label>
                    Opis životinje:
                    <input
                        type="text"
                        value={opis}
                        onChange={(e) => setOpis(e.target.value)}
                        placeholder="Unesi opis životinje"
                    />
                </label>
                <button type="submit">Pošalji</button>
            </form>
        </div>
    );
}

export default UnosPodataka;
