import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';

const App = () => {
  return (
    <View style={styles.container}>
      <Text style={styles.header}>Aplikacija za lov</Text>
      <View style={styles.menu}>
        <Button text="EVIDENCIJA LOVACA" />
        <Button text="RASPORED AKTIVNOSTI I LOVA" />
        <Button text="EVIDENCIJA PRISUTNOSTI NA LOVNI DAN" />
        <Button text="UNOS DIVLJAČI" />
        <Button text="UNOS ODSTRIJELENE DIVLJAČI" />
        <Button text="BODOVI" />
      </View>
    </View>
  );
};

const Button = ({ text }) => {
  return (
    <TouchableOpacity style={styles.button}>
      <Text style={styles.buttonText}>{text}</Text>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  header: {
    textAlign: 'center',
    margin: 20,
    fontSize: 22,
    fontWeight: 'bold',
  },
  menu: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    alignItems: 'center',
  },
  button: {
    backgroundColor: '#e7e7e7',
    paddingVertical: 15,
    paddingHorizontal: 10,
    margin: 10,
    borderRadius: 5,
    width: '40%', // width of the button
    // To match your sketch, we assume the buttons take up nearly half the width of the screen
    alignItems: 'center',
  },
  buttonText: {
    textAlign: 'center',
    fontWeight: 'bold',
  },
});

export default App;
