import React, { useState } from 'react';
import { View, TextInput, Button, StyleSheet, Text } from 'react-native';

const Bodovi = () => {
  const [brojIskaznice, setBrojIskaznice] = useState('');
  const [brojBodova, setBrojBodova] = useState('');
  const [opisBoda, setOpisBoda] = useState('');

  const unesiBodove = () => {
    // Ovdje implementirajte logiku za unos bodova
    console.log(brojIskaznice, brojBodova, opisBoda);
  };

  return (
    <View style={styles.container}>
      <TextInput
        placeholder="Broj članske iskaznice"
        value={brojIskaznice}
        onChangeText={setBrojIskaznice}
        style={styles.input}
      />
      <TextInput
        placeholder="Broj bodova"
        value={brojBodova}
        onChangeText={setBrojBodova}
        style={styles.input}
        keyboardType="numeric"
      />
      <TextInput
        placeholder="Opis dodijeljenog boda"
        value={opisBoda}
        onChangeText={setOpisBoda}
        style={[styles.input, styles.opisInput]}
        multiline
      />
      <Button title="UNESI" onPress={unesiBodove} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    padding: 20,
  },
  input: {
    height: 40,
    width: '100%',
    marginVertical: 10,
    borderWidth: 1,
    padding: 10,
  },
  opisInput: {
    height: 100, // Veća visina za opis
  },
});

export default Bodovi;
