import React, { useState, useEffect } from 'react';
import { QRCodeSVG } from 'qrcode.react';
import { jwtDecode } from 'jwt-decode';

function QRCodeDisplay() {
    const [huntersLicenseNumber, setHuntersLicenseNumber] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            setError('No token available. Please log in again.');
            return;
        }

        try {
            // Pretpostavljamo da `decoded.id` predstavlja broj lovačke iskaznice
            const decoded = jwtDecode(token);
            if (decoded && decoded.id) {
                setHuntersLicenseNumber(decoded.id); // Postavljanje broja lovačke iskaznice za generiranje QR koda
            } else {
                setError('License number is missing in the token.');
            }
        } catch (error) {
            console.error("Error decoding token:", error);
            setError('Failed to decode token.');
        }
    }, []);

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h1>Your QR Code</h1>
            {huntersLicenseNumber ? (
                // Generira QR kod koji sadrži samo broj lovačke iskaznice
                <QRCodeSVG value={huntersLicenseNumber} size={256} />
            ) : (
                <p>Loading QR Code...</p>
            )}
        </div>
    );
}

export default QRCodeDisplay;
