import React, { useState } from 'react';
import { QrReader } from 'react-qr-reader';

function QRCodeScanner() {
    const [qrResult, setQrResult] = useState('');
    const [isCameraOpen, setIsCameraOpen] = useState(false);

    const handleScan = data => {
        if (data) {
            setQrResult(data);
            setIsCameraOpen(false);  // Zatvara kameru nakon uspješnog skeniranja
        }
    };

    const handleError = err => {
        console.error(err);
        setIsCameraOpen(false);  // Zatvara kameru ako dođe do greške
    };

    const handleButtonClick = () => {
        setIsCameraOpen(true);  // Otvori kameru kada korisnik pritisne gumb
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Scan QR Code"
                value={qrResult}
                onChange={(e) => setQrResult(e.target.value)}
            />
            <button onClick={handleButtonClick}>Open Camera to Scan QR Code</button>
            {isCameraOpen && (
                <QrReader
                    delay={300}
                    onError={handleError}
                    onScan={handleScan}
                    style={{ width: '100%' }}
                />
            )}
        </div>
    );
}

export default QRCodeScanner;
