import React, { useState } from 'react';

function UnosPodataka() {
    const [input1, setInput1] = useState('');
    const [input2, setInput2] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('/api/submit', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ input1, input2 }),
            });
            const data = await response.json();
            console.log('Podaci su poslani:', data);
            alert('Podaci su uspješno spremljeni.');
            setInput1('');
            setInput2('');
        } catch (error) {
            console.error('Greška pri slanju podataka:', error);
            alert('Došlo je do greške pri slanju podataka.');
        }
    };

    return (
        <div>
            <h1>Unos Podataka</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Prvi podatak:
                    <input
                        type="text"
                        value={input1}
                        onChange={(e) => setInput1(e.target.value)}
                        placeholder="Unesi prvi tekst"
                    />
                </label>
                <label>
                    Drugi podatak:
                    <input
                        type="text"
                        value={input2}
                        onChange={(e) => setInput2(e.target.value)}
                        placeholder="Unesi drugi tekst"
                    />
                </label>
                <button type="submit">Pošalji</button>
            </form>
        </div>
    );
}

export default UnosPodataka;
