import React from 'react';
import { BrowserRouter as Router, Link, useNavigate } from 'react-router-dom';
import AppRoutes from './router/Routes';
import './App.css'; // Ensure styles are appropriately defined in CSS

function App() {
    const navigate = useNavigate();

    // Reusable button style
    const buttonStyle = {
        padding: '5px 10px',
        fontSize: '12px',
        color: 'white',
        backgroundColor: '#007BFF',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer'
    };

    return (
        <Router>
            <div className="app-container">
                <h1>Aplikacija za lov</h1>
                <div className="button-container">
                    <button style={buttonStyle} onClick={() => navigate('/popis-lovaca')}>Popis lovaca</button>
                    <button style={buttonStyle} onClick={() => navigate('/raspored-aktivnosti')}>Raspored</button>
                    <button style={buttonStyle} onClick={() => navigate('/Prisutnost')}>Prisutnost u lovu</button>
                    <button style={buttonStyle} onClick={() => navigate('/popis-divljaci')}>Popis divljaci</button>
                    <button style={buttonStyle} onClick={() => navigate('/popis-ostrjela')}>Ostrjel</button>
                    <button style={buttonStyle} onClick={() => navigate('/popis-bodova')}>Bodovi</button>
                    <LogoutButton />
                </div>

                <AppRoutes />
            </div>
        </Router>
    );
}

function LogoutButton() {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.clear(); // Clears all local storage data
        navigate('/login'); // Navigates to login page
    };

    // Apply the same button style
    const buttonStyle = {
        padding: '5px 10px',
        fontSize: '12px',
        color: 'white',
        backgroundColor: '#FF6347',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer'
    };

    return (
        <button onClick={handleLogout} style={buttonStyle}>
            Odjava
        </button>
    );
}

export default App;
