import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";

function DataEntryForm() {
    const [zivotinje, setZivotinje] = useState([]);
    const [sifraZivotinje, setSifraZivotinje] = useState('');
    const [lokacijaOdstrela, setLokacijaOdstrela] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://localhost:3000/zivotinje')
            .then(response => setZivotinje(response.data))
            .catch(error => console.error('Error fetching animals:', error));
    }, []);

    const handleSubmit = (event) => {
        event.preventDefault();
        const data = {
            sifra_zivotinje: sifraZivotinje,
            lokacija_odstrela: lokacijaOdstrela
        };

        // Retrieve the token from local storage
        const token = localStorage.getItem('token');

        // Set up the configuration for the axios request including the Authorization header
        const config = {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        };

        axios.post('http://localhost:3000/unos-ostreljene-zivotinje', data, config)
            .then(response => {
                alert('Odstreljena životinja je dodana.');
                navigate("/popis-ostreljenih");
            })
            .catch(error => {
                console.error('There was an error!', error);
                setMessage(`Error: ${error.message}`);
            });
    };

    // Styles can be moved to CSS files or kept inline for clarity in this example
    const formStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        margin: '50px',
        padding: '20px',
        borderRadius: '8px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
        backgroundColor: '#f7f7f7'
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#eee' }}>
            <form onSubmit={handleSubmit} style={formStyle}>
                <h1>Unos Odstreljene Životinje</h1>
                <div>
                    <label>Vrsta životinje:</label>
                    <select
                        value={sifraZivotinje}
                        onChange={(e) => setSifraZivotinje(e.target.value)}
                        required
                        style={{ margin: '10px 0', padding: '10px', width: '300px' }}
                    >
                        {zivotinje.map(zivotinja => (
                            <option key={zivotinja.sifra_zivotinje} value={zivotinja.sifra_zivotinje}>
                                {zivotinja.vrsta_zivotinje}
                            </option>
                        ))}
                    </select>
                </div>
                <div>
                    <label>Lokacija odstrela:</label>
                    <input
                        type="text"
                        value={lokacijaOdstrela}
                        onChange={(e) => setLokacijaOdstrela(e.target.value)}
                        required
                        style={{ margin: '10px 0', padding: '10px', width: '300px' }}
                    />
                </div>
                <button type="submit" style={{ padding: '10px 20px', fontSize: '16px', color: 'white', backgroundColor: '#007BFF', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Dodaj Odstrel</button>
                {message && <p>{message}</p>}
            </form>
        </div>
    );
}

export default DataEntryForm;
