import React, { useState, useEffect } from 'react';
import { QRCodeSVG } from 'qrcode.react';
import { jwtDecode } from 'jwt-decode';

const QRCodeDisplay = ({ token }) => {
    const [huntingLicenseNumber, setHuntingLicenseNumber] = useState('');

    // Dekodiranje tokena da bismo izvukli broj lovačke iskaznice
    React.useEffect(() => {
        if (token) {
            try {
                const decoded = jwtDecode(token);
                setHuntingLicenseNumber(decoded.id);  // Pretpostavka da je 'id' broj lovačke iskaznice
            } catch (error) {
                console.error('Problem kod dekodiranja tokena:', error);
            }
        }
    }, [token]);

    return (
        <div>
            {huntingLicenseNumber ? (
                <div>
                    <p>Broj lovačke iskaznice: {huntingLicenseNumber}</p>
                    <QRCode value={huntingLicenseNumber.toString()} />
                </div>
            ) : (
                <p>Nema dostupnih podataka o lovačkoj iskaznici.</p>
            )}
        </div>
    );
};

export default QRCodeDisplay;
