import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

function ALovac() {
    const location = useLocation();
    const [lovac, setLovac] = useState({
        broj_lovacke_iskaznice: '',
        ime: '',
        prezime: '',
        adresa: '',
        datum_rodjenja: '',
        kontakt: '',
        korisnicko_ime: '',
        uloga: ''
    });

    useEffect(() => {
        // Check if location state exists and has lov data
        if (location.state && location.state.lov) {
            setLovac(location.state.lov);
        }
    }, [location.state]);

    return (
        <div style={{ padding: '20px' }}>
            <h1>Edit Hunter</h1>
            <form>
                <label>Broj iskaznice:</label>
                <input type="text" value={lovac.broj_lovacke_iskaznice} onChange={(e) => setLovac({ ...lovac, broj_lovacke_iskaznice: e.target.value })} />

                <label>Ime:</label>
                <input type="text" value={lovac.ime} onChange={(e) => setLovac({ ...lovac, ime: e.target.value })} />

                <label>Prezime:</label>
                <input type="text" value={lovac.prezime} onChange={(e) => setLovac({ ...lovac, prezime: e.target.value })} />

                <label>Adresa:</label>
                <input type="text" value={lovac.adresa} onChange={(e) => setLovac({ ...lovac, adresa: e.target.value })} />

                <label>Datum rođenja:</label>
                <input type="text" value={lovac.datum_rodjenja} onChange={(e) => setLovac({ ...lovac, datum_rodjenja: e.target.value })} />

                <label>Kontakt:</label>
                <input type="text" value={lovac.kontakt} onChange={(e) => setLovac({ ...lovac, kontakt: e.target.value })} />

                <label>Korisničko ime:</label>
                <input type="text" value={lovac.korisnicko_ime} onChange={(e) => setLovac({ ...lovac, korisnicko_ime: e.target.value })} />

                <label>Uloga:</label>
                <input type="text" value={lovac.uloga} onChange={(e) => setLovac({ ...lovac, uloga: e.target.value })} />

                <button type="submit">Save Changes</button>
            </form>
        </div>
    );
}

export default ALovac;
